package model;

public class Phim003 {
    private int id;
    private String tenphim;
    private String theloai;
    private String thoiluong;
    private String mota;
    private String thoigiansx;

    public Phim003() {
        super();
        // TODO Auto-generated constructor stub
    }

    public Phim003(int id, String tenphim, String theloai, String thoiluong, String mota, String thoigiansx) {
        super();
        this.id = id;
        this.tenphim = tenphim;
        this.theloai = theloai;
        this.thoiluong = thoiluong;
        this.mota = mota;
        this.thoigiansx = thoigiansx;
    }

    public int getId() {
        return id;
    }

    public void setId(int id) {
        this.id = id;
    }

    public String getTenphim() {
        return tenphim;
    }

    public void setTenphim(String tenphim) {
        this.tenphim = tenphim;
    }

    public String getTheloai() {
        return theloai;
    }

    public void setTheloai(String theloai) {
        this.theloai = theloai;
    }

    public String getThoiluong() {
        return thoiluong;
    }

    public void setThoiluong(String thoiluong) {
        this.thoiluong = thoiluong;
    }

    public String getMota() {
        return mota;
    }

    public void setMota(String mota) {
        this.mota = mota;
    }

    public String getThoigiansx() {
        return thoigiansx;
    }

    public void setThoigiansx(String thoigiansx) {
        this.thoigiansx = thoigiansx;
    }
}
